import { Component } from '@angular/core';
import { LoanService } from '../service/loan.service';

@Component({
  selector: 'app-loan-calculator',
  templateUrl: './loan-calculator.component.html',
  styleUrls: ['./loan-calculator.component.css'],
})
export class LoanCalculatorComponent {
  loanData = {
    loanAmount: 0,
    loanPeriod: 0,
    loanType: 'EDUCATION_LOAN',
    interestRate: 0,
  };

  constructor(private loanService: LoanService) {}

  calculateLoan() {
    // Implement loan calculation logic here
    // You can calculate the EMI or total repayment amount based on user input
  }

  submitLoan() {
    this.loanService.createLoan(this.loanData).subscribe((response) => {
      // Handle the response here, e.g., show a success message or navigate to the loan list
    });
  }
}
<div>
  <h2>Loan Calculator</h2>
  <form (ngSubmit)="calculateLoan()" #loanForm="ngForm">
    <div class="form-group">
      <label for="loanAmount">Loan Amount</label>
      <input type="number" id="loanAmount" name="loanAmount" [(ngModel)]="loanData.loanAmount" required>
    </div>

    <div class="form-group">
      <label for="loanPeriod">Loan Period (in months)</label>
      <input type="number" id="loanPeriod" name="loanPeriod" [(ngModel)]="loanData.loanPeriod" required>
    </div>

    <div class="form-group">
      <label for="loanType">Loan Type</label>
      <select id="loanType" name="loanType" [(ngModel)]="loanData.loanType" required>

        <option value="SPENDING_LOAN">Spending Loan</option>
        <option value="HOUSING_LOAN">Housing Loan</option>
        <option value="BUSINESS_LOAN">Business Loan</option>
        <option value="EDUCATION_LOAN">Education Loan</option>
      </select>
    </div>

    <div class="form-group">
      <label for="interestRate">Interest Rate (%)</label>
      <input type="number" id="interestRate" name="interestRate" [(ngModel)]="loanData.interestRate" required>
    </div>

    <button type="submit" [disabled]="loanForm.invalid">Calculate Loan</button>
    <button type="button" (click)="submitLoan()">Submit Loan</button>
  </form>
</div>
