import { Component, OnInit } from '@angular/core';
import { LoanService } from '../service/loan.service';

@Component({
  selector: 'app-loan-list',
  templateUrl: './loan-list.component.html',
  styleUrls: ['./loan-list.component.css'],
})
export class LoanListComponent implements OnInit {
  loans: any[] = [];

  constructor(private loanService: LoanService) {}

  ngOnInit() {
    this.loadLoans();
  }

  loadLoans() {
    this.loanService.getLoans().subscribe((data) => {

      this.loans = data;
    });
  }

  deleteLoan(loanId: number) {
    this.loanService.deleteLoan(loanId).subscribe(() => {
      this.loadLoans();
    });
  }
}
<div>
  <h2>Loan List</h2>
  <ul>
    <li *ngFor="let loan of loans">
      {{ loan.loanType }} Loan: {{ loan.loanAmount | currency:'USD':'symbol' }} ({{ loan.loanPeriod }} months)
      <button (click)="deleteLoan(loan.id)">Delete</button>
    </li>
  </ul>
</div>
